{% if (compare_versions(PKGVERSION_COMP, "4.14.0")) >= 0 %}
      LibRPM.rpmPopMacro(mc, n)
    {% else %}
      LibRPM.delMacro(mc, n)
    {% end %}

{% if (compare_versions(PKGVERSION_COMP, "4.14.0")) >= 0 %}
      LibRPM.rpmPushMacro(mc, n, o, b, level)
    {% else %}
      LibRPM.addMacro(mc, n, o, b, level)
    {% end %}

{% if (compare_versions(PKGVERSION_COMP, "4.9.0")) >= 0 %}
      LibRPM.rpmTagGetReturnType(v)
    {% else %}
      m = LibRPM.rpmTagGetType(v)
      TagReturnType.new(m & TagReturnType::MASK.value)
    {% end %}

{% if (compare_versions(PKGVERSION_COMP, "4.9.0")) >= 0 %}
      LibRPM.rpmTagGetTagType(v)
    {% else %}
      m = LibRPM.rpmTagGetType(v)
      TagType.new((m & ~TagReturnType::MASK.value).to_i32)
    {% end %}

if ptr.null?
  self.new(ptr, **opts)
else
  {% if true %}
          case LibRPM.rpmProblemGetType(ptr)
            {% for m in [["BADARCH", "BadArch"], ["BADOS", "BadOS"], ["PKG_INSTALLED", "PackageInstalled"], ["BADRELOCATE", "BadRelocate"], ["NEW_FILE_CONFLICT", "NewFileConflict"], ["FILE_CONFLICT", "FileConflict"], ["OLDPACKAGE", "OldPackage"], ["DISKSPACE", "DiskSpace"], ["DISKNODES", "DiskNodes"], ["REQUIRES", "Requires"], ["CONFLICT", "Conflicts"], ["OBSOLETES", "Obsoletes"], ["VERIFY", "Verify"]] %}
              {% tagname = m[0].id %}
              {% clsname = m[1].id %}
            when ProblemType::{{ tagname }}
              RPM::Problem::{{ clsname }}.new(ptr, **opts)
            {% end %}
          else
            self.new(ptr, **opts)
          end
        {% end %}
end

